pub fn set_idt_entry(idx: usize, handler: u64, selector: u16, type_attr: u8) {
    unsafe {
        IDT[idx].offset_low = (handler as u16);
        IDT[idx].offset_mid = ((handler >> 16) as u16);
        IDT[idx].offset_high = (handler >> 32) as u32;
        IDT[idx].selector = selector;
        IDT[idx].ist = 0;
        IDT[idx].type_attr = type_attr; // Interrupt gate
        IDT[idx].reserved = 0;
    }
}

