#custom-notifications {
    background-color: #303536;
    margin: 6px 6px 6px 6px;
    padding: 0px 4px 0px 4px;
    border: 2px solid #434a4c;
    color: #9e8b67;
}

#custom-notifications button {
    all: initial;
    min-width: 0;
    box-shadow: inset 0 -3px transparent;
    padding: 2px 4px;
    color: #c7ab7a;
}

#custom-notifications button.focused {
    color: #ddc7a1;
}

#custom-notifications button.urgent {
    background-color: #e78a4e;
}
